SELECT b.id, b.username, b.high, b.sex, b.weight, b.age, b.phone, b.product_id, q.answer_time, q.openid, q.id productPaperId, p.name productName FROM biz_customer_paper b JOIN qu_product_paper q ON b.id = q. USER JOIN biz_product p ON b.product_id=p.id AND q.answer_status = 1
and username like concat('%', #{username},'%') and answer_time >= #{startTime} and answer_time <= #{endTime} and product_id = #{productId} and phone = #{phone}
order by ${sort} ${order}
order by id desc
limit #{offset}, #{limit}

	SELECT
					count(*)
					FROM
						biz_customer_paper b
					JOIN qu_product_paper q ON b.id = q. USER
					AND q.answer_status = 1


  		    and username like concat('%', #{username},'%') 
  		    and answer_time >= #{startTime} 
  		    and answer_time <= #{endTime} 
  		    and product_id = #{productId} 
  		    and phone = #{phone}

SELECT n.id nid, n.category ncategory, n.content ncontent, n.type ntype, n.sort nsort, n.required nrequired, n.create_by ncreateBy, n.create_time ncreateTime, n.update_by nupdateBy, n.update_time nupdateTime, n.remark nremark, n.img nimg, n.contenw ncontenw, n.if_stop nifStop, n.paper_id npaperId, c.id cid, c.content ccontent,
c.create_by ccreateBy, c.create_time ccreateTime, c.update_by cupdateBy, c.update_time cupdateTime, c.remark cremark, t.id tid, t.product tproduct, t.score tscore, t.tadf ttadf, t.tads ttads, t.tadreason ttadreason, t.tadjianyi ttadjianyi, t.bmi tbmi, t.bmis tbmis, t.scores tscores, t.product_name tproductName, t.tadfs ttadfs, t.tadss ttadss, t.tadreasons ttadreasons, t.tadjianyis ttadjianyis, t.choose tchoose FROM (select * from qu_question where category=#{fenlei} and paper_id=(SELECT paper_id FROM biz_product WHERE id=#{product}) order by sort) n JOIN qu_choice c ON n.id = c.question JOIN (SELECT * FROM qu_choice_product WHERE PRODUCT=#{product}
and score is not null ) t on t.choose=c.id

		select `id`,`talk_name`,`score_big`,`score_little`,`talk_content`,`add_time`,`update_time`,`status`,`talk_type` from sys_report_talk

		  		   and id = #{id} 
		  		   and talk_name = #{talkName} 
		  		   and score_big = #{scoreBig} 
		  		   and score_little = #{scoreLittle} 
		  		   and talk_content = #{talkContent} 
		  		   and add_time = #{addTime} 
		  		   and update_time = #{updateTime} 
		  		   and status = #{status} 
		  		   and talk_type = #{talkType} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}
