mapper.select:
		select bp.id,bp.code,bp.url,bp.name,bp.description,bp.create_by,bp.create_time,bp.update_by,bp.update_time,bp.remark,bp.del_flag,
		bp.paper_name ,bp.paper_id
		from biz_product bp

		  		   and bp.id = #{id} 
		  		   and bp.code = #{code} 
		  		   and bp.url = #{url} 
		  		   and bp.name = #{name} 
		  		   and bp.description = #{description} 
		  		   and bp.create_by = #{createBy} 
		  		   and bp.create_time = #{createTime} 
		  		   and bp.update_by = #{updateBy} 
		  		   and bp.update_time = #{updateTime} 
		  		   and bp.remark = #{remark} 
		  		   and bp.del_flag = #{delFlag} 
		  		   and bp.paper_name = #{paperName} 
		  		   and bp.paper_id = #{paperId} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

