mapper.select:
SELECT n.id nid, n.category ncategory, n.content ncontent, n.type ntype, n.sort nsort, n.required nrequired, n.create_by ncreateBy, n.create_time ncreateTime, n.update_by nupdateBy, n.update_time nupdateTime, n.remark nremark, n.img nimg, n.contenw ncontenw, n.if_stop nifStop, n.paper_id npaperId, c.id cid, c.content ccontent,
c.create_by ccreateBy, c.create_time ccreateTime, c.update_by cupdateBy, c.update_time cupdateTime, c.remark cremark, t.id tid, t.product tproduct, t.score tscore, t.tadf ttadf, t.tads ttads, t.tadreason ttadreason, t.tadjianyi ttadjianyi, t.bmi tbmi, t.bmis tbmis, t.scores tscores, t.product_name tproductName, t.tadfs ttadfs, t.tadss ttadss, t.tadreasons ttadreasons, t.tadjianyis ttadjianyis, t.choose tchoose FROM (select * from qu_question where category=#{flag} and paper_id=(SELECT paper_id FROM biz_product WHERE id=#{product})) n JOIN qu_choice c ON n.id = c.question JOIN (SELECT * FROM qu_choice_product WHERE PRODUCT=#{product}
and score is not null ) t on t.choose=c.id
